import React, { useEffect,useMemo,useState } from 'react'
import {Grid, GridItem, Button, Divider,Spacer,Flex, Center, Box, Container , Spinner, Heading, Table, Tbody, Td, Th, Thead, Tr, FormControl,
  FormLabel,
  FormErrorMessage,
  FormHelperText,Input, Menu} from "@chakra-ui/react"
import axios from "axios"
import {useTable} from 'react-table'
import MouseSharpIcon from '@mui/icons-material/MouseSharp';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
const url = "http://localhost:5000/problems";

const fetchData = [
  {
    "id": 1,
    "name": "binary search",
    "level": "easy",
    "submission" : "39939"
  },
]

const tableColumn = [
  {
    Header: "ID",
    accessor : "id"
  },
  {
    Header: "NAME",
    accessor : "name"
  },
  {
    Header: "LEVEL",
    accessor : "level"
  },
  {
    Header: "SUBMISSION",
    accessor : "submission"
  },
  {
    Header : "START",
    accessor :(cellValues)=>{
      return (
        <Button size='sm' rightIcon={<MouseSharpIcon />} variant='outline' colorScheme='cyan'>
          Start
        </Button> 
      )
    }
  }
]

const Problem = () => {
  const [problems,setProblems] = useState([]);
  const[value,setValue] = useState([]);

  const columns = useMemo(()=>tableColumn,[])
  const data = useMemo(()=>problems,[problems])
  const {
    getTableProps,getTableBodyProps,headerGroups,rows,prepareRow
  } = useTable({
    columns: columns,
    data: data,
  })
  const handleSearch = async (e) => {
    e.preventDefault();
    return await axios.get('http://localhost:5000/problems?q=$')
  };

  useEffect(()=>{
    const fetchProblems = async () =>{
      try{
        const {data} = await axios.get(url);
        setProblems(data);
      }
      catch(error){
        console.log(error);
      }
    };
    fetchProblems();
  },[])
  console.log(problems);
  if(problems.length === 0) return (
    <Center>
      <Spinner/>
    </Center>
  );
  return (
    <>
      <Center>
        <Container  padding='4' maxW='6xl' >
            
                <form action = "" class="w-full max-w-md" onSubmit={handleSearch}>
                  <div class = "relative flex items-center text-gray-400 focus-within:text-gray-600">
                    <SearchOutlinedIcon class="w-5 h-5 absolute ml-3 pointer-events-none" />
                    <input
                      type="text"
                      name="search"
                      placeholder='Search'
                      autoComplete='off'
                      aria-label='Search'
                      class='pr-3 pl-10 py-2 font-semibold placeholder-gray-500 text-black rounded-2xl border-none ring-2 ring-gray-300 focus:ring-gray-500 focus:ring-2'
                      onChange={(e)=>setValue(e.target.value)}
                    />
                    <Button size='sm' variant='outline' colorScheme='cyan' type="submit">
                      Search
                    </Button> 
                  </div>
                </form>
                <Menu class ="relative flex-shrink-0"></Menu>
              
            <Flex minWidth='max-content' alignItems='center' gap='5'>
              <Spacer/>
              <Divider orientation='vertical' />
              <Heading maxW='32rem'>Problems</Heading>
              <Spacer/>
              <Divider orientation='vertical' />
              {/* <form style = {
                {
                  margin : "auto",
                  padding : "15px",
                  maxWidth : "400px",
                  alignContent : "center",
                  
                }}classname = "bg-white shadow-md rounded px-8 pt-6 pb-10 mb-4 flex j w-full" >
                <input
                  type = "text"
                  className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline '
                  placeholder='Search Name ...'
                />
              </form> */}
              

              <Spacer/>
              <Divider orientation='vertical' />
              <Heading maxW='32rem'>Problems</Heading>
              <Spacer/>
              
            </Flex>
          

            <Table variant = 'striped' colorScheme='cyan' {...getTableProps()}>
              <Thead>
                {
                  headerGroups.map((headerGroups)=>(
                    <Tr{...headerGroups.getHeaderGroupProps()}>
                      {headerGroups.headers.map((column)=>(
                        <Th fontSize="md" {...column.getHeaderProps()}>
                          {column.render("Header")}
                        </Th>
                      ))}
                    </Tr>
                  ))
                }
              </Thead>
              <Tbody {...getTableBodyProps()}>
                {
                  rows.map((row,i) => {
                    prepareRow(row);
                    return(
                      <Tr {...row.getRowProps()}>
                        {row.cells.map((cell)=>(
                          
                          <Td {...cell.getCellProps()}>
                            {cell.render("Cell")}
                            
                          </Td>  
                        ))}
                        {/* <Button m={[12, 3]} size='sm' rightIcon={<MouseSharpIcon />} variant='outline' height='45px'
                                width='150px'
                                border='2px'
                                borderColor='orange'>
                          Start
                        </Button> */}
                        
                      </Tr>
                    )
                  })
                }
              
              </Tbody>
            </Table>
          
      
        </Container>
      </Center>
    </>
  )
}

export default Problem
