import React, { useEffect,useMemo,useState } from 'react'
import {Button, Divider,Spacer,Flex, Center, Box, Container , Spinner, Heading, Table, Tbody, Td, Th, Thead, Tr} from "@chakra-ui/react"
import axios from "axios"
import {useTable} from 'react-table'
import MouseSharpIcon from '@mui/icons-material/MouseSharp';
const url = "http://localhost:5000/problems";

const fetchData = [
  {
    "id": 1,
    "name": "binary search",
    "level": "easy",
    "submission" : "39939"
  },
]

const tableColumn = [
  {
    Header: "ID",
    accessor : "id"
  },
  {
    Header: "NAME",
    accessor : "name"
  },
  {
    Header: "LEVEL",
    accessor : "level"
  },
  {
    Header: "SUBMISSION",
    accessor : "submission"
  }
]

const Problem = () => {
  const [products,setProducts] = useState([]);
  const columns = useMemo(()=>tableColumn,[])
  const data = useMemo(()=>products,[products])
  const {
    getTableProps,getTableBodyProps,headerGroups,rows,prepareRow
  } = useTable({
    columns: columns,
    data: data,
  })

  useEffect(()=>{
    const fetchProducts = async () =>{
      try{
        const {data} = await axios.get(url);
        setProducts(data);
      }
      catch(error){
        console.log(error);
      }
    };
    fetchProducts();
  },[])
  console.log(products);
  if(products.length === 0) return (
    <Center>
      <Spinner/>
    </Center>
  );
  return (
    <>
      <Center>
      <Container padding='4' maxW='6xl' >
      
      <Flex>
        <Divider orientation='vertical' />
        <Heading maxW='32rem'>Problems</Heading>
        <Spacer/>
        <Divider orientation='vertical' />
        <Heading maxW='32rem'>Problems</Heading>
        <Spacer/>
        <Divider orientation='vertical' />
        <Heading maxW='32rem'>Problems</Heading>
      </Flex>
      
      
      <Table size='md' variant = 'striped' colorScheme='orange' {...getTableProps()}>
        <Thead>
          {
            headerGroups.map((headerGroups)=>(
              <Tr{...headerGroups.getHeaderGroupProps()}>
                {headerGroups.headers.map((column)=>(
                  <Th fontSize="md" {...column.getHeaderProps()}>
                    {column.render("Header")}
                  </Th>
                ))}
              </Tr>
            ))
          }
        </Thead>
        <Tbody {...getTableBodyProps()}>
          {
            rows.map((row,i) => {
              prepareRow(row);
              return(
                <Tr {...row.getRowProps()}>
                  {row.cells.map((cell)=>(
                    
                    <Td {...cell.getCellProps()}>
                      {cell.render("Cell")}
                      
                    </Td>  
                  ))}
                  <Button m={[12, 33]} size='sm' rightIcon={<MouseSharpIcon />} variant='outline' height='45px'
                          width='150px'
                          border='2px'
                          borderColor='orange'>
                    Start
                  </Button>
                  
                </Tr>
              )
            })
          }
         
        </Tbody>
      </Table>
     
      </Container>
      </Center>
    </>
  )
}

export default Problem
