import React, { useEffect,useMemo,useState } from 'react'
import {Grid,SimpleGrid, GridItem, Button, Divider,Spacer,Flex, Center, Box, Container , Spinner, Heading, Table, Tbody, Td, Th, Thead, Tr, FormControl,FormLabel,Text,FormErrorMessage,FormHelperText,Input, Menu, Tooltip, Icon, IconButton, Select} from "@chakra-ui/react"
import axios from "axios"
import {useTable,usePagination} from 'react-table'
import MouseSharpIcon from '@mui/icons-material/MouseSharp';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { ArrowLeftIcon, ArrowRightIcon, ChevronLeftIcon, ChevronRightIcon } from '@chakra-ui/icons';

const url = "http://localhost:5000/problems";

const fetchData = [
  {
    "id": 1,
    "name": "binary search",
    "level": "easy",
    "submission" : "39939"
  },
]

const tableColumn = [
  {
    Header: "ID",
    accessor : "id"
  },
  {
    Header: "NAME",
    accessor : "name"
  },
  {
    Header: "LEVEL",
    accessor : "level"
  },
  {
    Header: "SUBMISSION",
    accessor : "submission"
  },
  {
    Header : "START",
    accessor :(cellValues)=>{
      return (
        <Button size='sm' rightIcon={<MouseSharpIcon />} variant='outline' colorScheme='cyan'>
          Start
        </Button> 
        
        
      )
    }
  }
]

const Problem = () => {
  const [problems,setProblems] = useState([]);
  const[value,setValue] = useState("");
  const[sortValue, setSortValue] = useState("");

  const sortOptions = ['id','submission']

  const columns = useMemo(()=>tableColumn,[])
  const data = useMemo(()=>problems,[problems])
  const 
  {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    gotoPage,
    nextPage,
    prevPage,
    pageCount,
    page,
    pageOptions,
    setPageSize,
    state:{
      pageIndex,
      pageSize
    },
  } = useTable({
          columns,
          data,
          initialState:{pageIndex:1},
        },
        usePagination
        );
  
  useEffect(()=>{fetchProblems();},[])

  const fetchProblems = async () =>{
    try{
      const {data} = await axios.get(url).then((response)=>setProblems(response.data)).catch((err)=>console.log(err));
    }
    catch(error){
      console.log(error);
    }
  };
  console.log(problems);

  const handleSearch = async (e) => {
    e.preventDefault();
    return await axios.get(`http://localhost:5000/problems?q=${value}`).then((response) => {
      setProblems(response.data);
      setValue("")
    })
    .catch((err)=>console.log(err));
  };
  const handleReset = (e) => { fetchProblems(); };

  const handleSort = async (e) => {
    let value = e.target.value;
    setSortValue(value);
    return await axios.get(`http://localhost:5000/problems?_sort=${value}&_order=asc`).then((response) => {
      setProblems(response.data);
    })
    .catch((err)=>console.log(err));
  };
  
  const handleFilter = async (value) => {
    return await axios.get(`http://localhost:5000/problems?status=${value}`).then((response) => {
      setProblems(response.data);
    })
    .catch((err)=>console.log(err));
  };

  if(problems.length === 0) return (
    <Center>
      <Spinner/>
    </Center>
  );

  return (
    <>
      <Center>
        <Container  padding='4' maxW='6xl' >
          <div>
            <form action = "" class="w-full max-w-md" onSubmit={handleSearch}>
              <div class = "relative flex items-center text-gray-400 focus-within:text-gray-600">
                <SearchOutlinedIcon class="w-5 h-5 absolute ml-3 pointer-events-none" />
                <input
                  type="text"
                  name="search"
                  placeholder='Search'
                  autoComplete='off'
                  aria-label='Search'
                  class='pr-3 pl-10 py-2 font-semibold placeholder-gray-500 text-black rounded-2xl border-none ring-2 ring-gray-300 focus:ring-gray-500 focus:ring-2'
                  value={value}
                  onChange={(e)=>setValue(e.target.value)}
                />
                <Button size='sm' variant='outline' colorScheme='cyan' type="submit">
                  Search
                </Button> 
                <Button size='sm' variant='outline' colorScheme='cyan' type="submit" onClick={() => handleReset()}>
                  Reset
                </Button> 
              </div>
            </form>
              
            <Flex minWidth='max-content' alignItems='center' gap='5'>
              <Spacer/>
              <Divider orientation='vertical' />
              <Heading maxW='32rem'>Problems</Heading>
              <Spacer/>
              <Divider orientation='vertical' />
              {/* <form style = {
                {
                  margin : "auto",
                  padding : "15px",
                  maxWidth : "400px",
                  alignContent : "center",
                  
                }}classname = "bg-white shadow-md rounded px-8 pt-6 pb-10 mb-4 flex j w-full" >
                <input
                  type = "text"
                  className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline '
                  placeholder='Search Name ...'
                />
              </form> */}
              <Spacer/>
              <Divider orientation='vertical' />
              <Heading maxW='32rem'>Problems</Heading>
              <Spacer/>
              
            </Flex>
          

            <Table variant = 'striped' colorScheme='cyan' {...getTableProps()}>
              <Thead>
                {
                  headerGroups.map((headerGroups)=>(
                    <Tr{...headerGroups.getHeaderGroupProps()}>
                      {headerGroups.headers.map((column)=>(
                        <Th fontSize="md" {...column.getHeaderProps()}>
                          {column.render("Header")}
                        </Th>
                      ))}
                    </Tr>
                  ))
                }
              </Thead>
              <Tbody {...getTableBodyProps()}>
                {
                  page.map((row,i) => {
                    prepareRow(row);
                    return(
                      <Tr {...row.getRowProps()}>
                        {row.cells.map((cell)=>(
                          
                          <Td {...cell.getCellProps()}>
                            {cell.render("Cell")}
                            
                          </Td>  
                        ))}
                        {/* <Button m={[12, 3]} size='sm' rightIcon={<MouseSharpIcon />} variant='outline' height='45px'
                                width='150px'
                                border='2px'
                                borderColor='orange'>
                          Start
                        </Button> */}
                        
                      </Tr>
                    )
                  })
                }
              
              </Tbody>
            </Table>

            <Flex justify="space-between" m={4} align="center">
              <Flex gap="4">
                <Tooltip label="First Page">
                  <IconButton onClick={() => gotoPage(0)} icon = {<ArrowLeftIcon h="3" w="3"/>} />
                </Tooltip>
                <Tooltip label = "Prev Page">
                  <IconButton 
                    onClick={prevPage} 
                    icon = {<ChevronLeftIcon h="3" w="3"/>} 
                  />
                </Tooltip>
              </Flex>
              
              <Flex allign = "center">
                <Flex align = "center" gap = "2">
                  Page{" "}
                  <Text fontWeight="bold" as = "span">
                    {pageIndex + 1}
                  </Text>
                  of
                  <Text fontWeight="bold" as = "span">
                    {pageOptions.length}
                  </Text>
                </Flex>

                <Select w = "32" value = {pageSize} onChange={
                  (e) => {
                    setPageSize(Number(e))
                  }
                }>
                  
                </Select>
              </Flex>

              <Flex gap="4">
                <Tooltip label="Next Page">
                  <IconButton onClick={nextPage} icon = {<ArrowRightIcon h="3" w="3"/>} />
                </Tooltip>
                <Tooltip label = "Last Page">
                  <IconButton 
                    onClick={()=>{
                      gotoPage(pageCount-1);
                    }} 
                    icon = {<ChevronRightIcon h="3" w="3"/>} 
                  />
                </Tooltip>
                
              </Flex> 
            </Flex>
          
          </div>
          <div mt-6>
            {/* <Grid templateColumns='repeat(5, 1fr)' gap={4}>
              <GridItem w='100%' h='10'/>Sort
              <GridItem w='100%' h='10'/> Filter
            </Grid> */}
            <SimpleGrid minChildWidth='120px' spacing='40px'>
              <Box height='80px'><Heading>Sort By:</Heading></Box>
              <Box height='80px'>
                <select style ={{width: "50%", borderRadius:"2px", height:"35px"}}
                onChange={handleSort}
                value = {sortValue}
                >
                  <option>Please Select Value</option>
                  {sortOptions.map((item,i)=>(
                    <option value= {item} key={i}> {item}</option>
                  ))}
                </select>
              </Box>
              <Box height='80px'><Heading>Filter By:</Heading></Box>
              <Box height='80px'>
                <Button size='sm' variant='outline' colorScheme='cyan' onClick={() => handleFilter("Easy")}>
                  Easy
                </Button>
                <Button size='sm' variant='outline' colorScheme='cyan'  onClick={() => handleFilter("Medium")}>
                  Medium
                </Button>
                <Button size='sm' variant='outline' colorScheme='cyan'onClick={() => handleFilter("Hard")}>
                  Hard
                </Button>
              </Box>
            </SimpleGrid>
          </div>
        </Container>
      </Center>
    </>
  )
}

export default Problem
