import React, { useEffect,useMemo,useState } from 'react'
import {Divider,Stack,Text, Center, Box, Container , Spinner, Heading, Table, Tbody, Td, Th, Thead, Tr} from "@chakra-ui/react"
import axios from "axios"
import {useTable} from 'react-table'
const url = "http://localhost:5000/problems";

const fetchData = [
  {
    "id": 1,
    "name": "binary search",
    "level": "easy"
  },
]

const tableColumn = [
  {
    Header: "ID",
    accessor : "id"
  },
  {
    Header: "NAME",
    accessor : "name"
  },
  {
    Header: "LEVEL",
    accessor : "level"
  }
]

const Problem = () => {
  const [products,setProducts] = useState([]);
  const columns = useMemo(()=>tableColumn,[])
  const data = useMemo(()=>products,[products])
  const {
    getTableProps,getTableBodyProps,headerGroups,rows,prepareRow
  } = useTable({
    columns: columns,
    data: data,
  })

  useEffect(()=>{
    const fetchProducts = async () =>{
      try{
        const {data} = await axios.get(url);
        setProducts(data);
      }
      catch(error){
        console.log(error);
      }
    };
    fetchProducts();
  },[])
  console.log(products);
  if(products.length === 0) return (
    <Center>
      <Spinner/>
    </Center>
  );
  return (
    <>
      
      <Container maxW='6xl' centerContent>
      <Box padding='4' w='100%' >
      <Stack direction='row' h='70px' p={4} spacing='144px'>
        <Divider orientation='vertical' />
        <Heading maxW='32rem'>Problems</Heading>
        <Divider orientation='vertical' />
        <Heading maxW='32rem'>Problems</Heading>
      </Stack>
      
      {/* </Center> */}
      <Table variant = 'striped' colorScheme='orange' {...getTableProps()}>
        <Thead>
          {
            headerGroups.map((headerGroups)=>(
              <Tr{...headerGroups.getHeaderGroupProps()}>
                {headerGroups.headers.map((column)=>(
                  <Th {...column.getHeaderProps()}>
                    {column.render("Header")}
                  </Th>
                ))}
              </Tr>
            ))
          }
        </Thead>
        <Tbody {...getTableBodyProps()}>
          {
            rows.map((row,i) => {
              prepareRow(row);
              return(
                <Tr {...row.getRowProps()}>
                  {row.cells.map((cell)=>(
                    <Td {...cell.getCellProps()}>
                      {cell.render("Cell")}
                    </Td>
                  ))}
                </Tr>
              )
            })
          }
        </Tbody>
      </Table>
      </Box>
      </Container>
    </>
  )
}

export default Problem
